public class Ex1DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a = 127;
		byte b = (byte)128;
		byte c = (byte)200;
		byte d = (byte)161;
		System.out.println("a = " + a);
		System.out.println("b = " + b);
		System.out.println("c = " + c);
		System.out.println("d = " + d);
		
	}

}
